curl -s https://ispip.clang.cn/all_cn_cidr.txt | sed -e 's/^/add address=/g' -e 's/$/ list=LOCALIP/g' >1.rsc
curl -s https://raw.githubusercontent.com/17mon/china_ip_list/master/china_ip_list.txt | sed -e 's/^/add address=/g' -e 's/$/ list=LOCALIP/g' >2.rsc
